static const char* print_level_to_str(PrintLevel print_level)
{
  switch (print_level)
    {
	case RPRT_NOP:		{ return "RPRT_NOP"; break; }
	case RPRT_DEBUG_3:		{ return "RPRT_DEBUG_3"; break; }
	case RPRT_DEBUG_2:		{ return "RPRT_DEBUG_2"; break; }
	case RPRT_DEBUG_1:		{ return "RPRT_DEBUG_1"; break; }
	case RPRT_INFO:		{ return "RPRT_INFO"; break; }
	case RPRT_NONMASKABLE:		{ return "RPRT_NONMASKABLE"; break; }
	default:		{ return "<INVALID>"; break; }
    }
}

static const char* error_level_to_str(ErrorLevel el)
{
  switch (el)
    {
	case RERR_MESSAGE:		{ return "RERR_MESSAGE"; break; }
	case RERR_WARNING:		{ return "RERR_WARNING"; break; }
	case RERR_ERROR:		{ return "RERR_ERROR"; break; }
	default:		{ return "<INVALID>"; break; }
    }
}

