// Validation

static int is_print_level_valid(PrintLevel pl)
{
  switch (pl)
    {
	case RPRT_NOP:		{ return 1; break; }
	case RPRT_DEBUG_3:		{ return 1; break; }
	case RPRT_DEBUG_2:		{ return 1; break; }
	case RPRT_DEBUG_1:		{ return 1; break; }
	case RPRT_INFO:		{ return 1; break; }
	case RPRT_NONMASKABLE:		{ return 1; break; }
	default:		{ return 0; break; }
    }
}

static int is_error_level_valid(ErrorLevel el)
{
  switch (el)
    {  
	case RERR_MESSAGE:		{ return 1; break; }
	case RERR_WARNING:		{ return 1; break; }
	case RERR_ERROR:		{ return 1; break; }
	default:		{ return 0; break; }
   }
}

static int is_report_type_valid(ReportType type)
{
	return (type < __RTYP_LAST_TYPE);
}

